import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function PlayerSearch() {
  const [searchQuery, setSearchQuery] = useState("");
  const [allPlayers, setAllPlayers] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPlayers, setSelectedPlayers] = useState([]);
  const [error, setError] = useState(null);

  const location = useLocation();
  const leagueSettings = location.state?.leagueSettings;

  useEffect(() => {
    // Fetch all players from the backend
    fetch('http://127.0.0.1:5000/api/all-players')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        if (data.error) {
          throw new Error(data.error);
        }
        setAllPlayers(data);
      })
      .catch(error => {
        console.error('Error fetching players:', error);
        setError('An error occurred while fetching players. Please try again later.');
      });
  }, []);


  const addPlayer = (player) => {
    setSelectedPlayers([...selectedPlayers, player]);
    setSearchQuery("");
    setSearchResults([]);
  };

  const removePlayer = (playerToRemove) => {
    setSelectedPlayers(selectedPlayers.filter(player => player.espnPlayerId !== playerToRemove.espnPlayerId));
  };


  return (
    <div className="Search">
      <header className="Search-header">
        <p>{message}</p>
        <h2>Search and Add Players</h2>
        <div className="search-container">
          <input 
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search players"
            className="search-input"
          />
          {searchResults.length > 0 && (
            <div className="search-results-container">
              <div className="search-results">
                {searchResults.map((player, index) => (
                  <div key={index} className="search-result-item" onClick={() => addPlayer(player)}>
                    {player.playerName} - {player.position} - {player.teamName} - Age: {player.age}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
        {error && <p style={{color: 'red'}}>{error}</p>}

        <h2>Your Lineup</h2>
        <ul className="selected-players">
          {selectedPlayers.map((player, index) => (
            <li key={index}>
              {player.playerName} - {player.position} - {player.teamName} - Age: {player.age}
              <button onClick={() => removePlayer(player)}>Remove</button>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}


export default PlayerSearch;
