import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [allPlayers, setAllPlayers] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPlayers, setSelectedPlayers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/api/all-players')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        if (data.error) {
          throw new Error(data.error);
        }
        setAllPlayers(data);
      })
      .catch(error => {
        console.error('Error fetching players:', error);
        setError('An error occurred while fetching players. Please try again later.');
      });
  }, []);

  useEffect(() => {
    if (searchQuery.length > 1) {
      const results = allPlayers.filter(player => 
        player.playerName.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setSearchResults(results.slice(0, 10));
    } else {
      setSearchResults([]);
    }
  }, [searchQuery, allPlayers]);

  const addPlayer = (player) => {
    setSelectedPlayers([...selectedPlayers, player]);
    setSearchQuery("");
    setSearchResults([]);
  };

  const removePlayer = (playerToRemove) => {
    setSelectedPlayers(selectedPlayers.filter(player => player.espnPlayerId !== playerToRemove.espnPlayerId));
  };

  const [teamName, setTeamName] = useState("");
  const [scoringType, setScoringType] = useState("standard");
  const [numberOfTeams, setNumberOfTeams] = useState(10);
  const [starters, setStarters] = useState({
    qb: 1, rb: 2, wr: 2, te: 1, flex: 1, def: 1, k: 1
  });
  const [benchSpots, setBenchSpots] = useState(6);

  const handleStartersChange = (position, value) => {
    setStarters(prev => ({ ...prev, [position]: parseInt(value) }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can handle the form submission, e.g., send data to backend
    console.log({ teamName, scoringType, numberOfTeams, starters, benchSpots });
  };  

  return (
    <div className="App">
      <header className="App-header">
        <p>{message}</p>
        <h2>League Settings</h2>
<form onSubmit={handleSubmit} className="league-settings-form">
  <div>
    <label htmlFor="teamName">Team Name:</label>
    <input
      type="text"
      id="teamName"
      value={teamName}
      onChange={(e) => setTeamName(e.target.value)}
      required
    />
  </div>
  <div>
    <label htmlFor="scoringType">Scoring Type:</label>
    <select
      id="scoringType"
      value={scoringType}
      onChange={(e) => setScoringType(e.target.value)}
    >
      <option value="standard">Standard</option>
      <option value="ppr">PPR</option>
      <option value="halfppr">Half PPR</option>
    </select>
  </div>
  <div>
    <label htmlFor="numberOfTeams">Number of Teams:</label>
    <input
      type="number"
      id="numberOfTeams"
      value={numberOfTeams}
      onChange={(e) => setNumberOfTeams(parseInt(e.target.value))}
      min="4"
      max="20"
      required
    />
  </div>
  <div>
    <h3>Number of Starters</h3>
    {Object.entries(starters).map(([position, count]) => (
      <div key={position}>
        <label htmlFor={position}>{position.toUpperCase()}:</label>
        <input
          type="number"
          id={position}
          value={count}
          onChange={(e) => handleStartersChange(position, e.target.value)}
          min="0"
          max={position === "qb" ? "2" : "4"}
          required
        />
      </div>
    ))}
  </div>
  <div>
    <label htmlFor="benchSpots">Bench Spots:</label>
    <input
      type="number"
      id="benchSpots"
      value={benchSpots}
      onChange={(e) => setBenchSpots(parseInt(e.target.value))}
      min="0"
      max="10"
      required
    />
  </div>
  <button type="submit">Save League Settings</button>
</form>
        <h2>Search and Add Players</h2>
        <div className="search-container">
          <input 
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search players"
            className="search-input"
          />
          {searchResults.length > 0 && (
            <div className="search-results-container">
              <div className="search-results">
                {searchResults.map((player, index) => (
                  <div key={index} className="search-result-item" onClick={() => addPlayer(player)}>
                    {player.playerName} - {player.position} - {player.teamName} - Age: {player.age}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
        {error && <p style={{color: 'red'}}>{error}</p>}

        <h2>Your Lineup</h2>
        <ul className="selected-players">
          {selectedPlayers.map((player, index) => (
            <li key={index}>
              {player.playerName} - {player.position} - {player.teamName} - Age: {player.age}
              <button onClick={() => removePlayer(player)}>Remove</button>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
